import React, { Component } from "react";
import Home from "./components/Home/Home";
import JobApplication from "./components/JobApplication/JobApplication";
import ScrollToTop from "./ScrollToTop";
import JobForm from "./components/JobForm/JobForm"
import JobList from "./components/JobList/JobList"
import "./App.scss";
import "react-toastify/dist/ReactToastify.css";

import { BrowserRouter, Switch, Route, Redirect } from "react-router-dom";

class App extends Component {
  constructor() {
    super();
    this.state = {};
  }

  componentDidMount(){
    this.Test1()
  }

  Test1 = () => {
    var arr = [1,2,4,3,4,5,1,2]
    var tempArr = []

    for(var i=0;i<arr.length;i++){
      var counter = 0;
      if(tempArr.includes(arr[i])){
        return;
      }else{
        for(var j=0;j<arr.length;j++){
          if(arr[i] === arr[j]){
            counter += 1;
          }
        }
        tempArr.con(arr[i]);
        console.log(arr[i],"->",counter);
      }
    }
  }

  render() {
    return (
      <div className="App">
        <BrowserRouter>
          <ScrollToTop>
            <Switch>
              <Route exact path="/home" component={Home} />
              <Route exact path="/career" component={JobApplication} />
              <Route exact path="/job-list" component={JobList} />
              <Route exact path="/apply-job" component={JobForm} />
              <Route render={() => <Redirect to="/home" />} />
            </Switch>
          </ScrollToTop>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
